sys_variable_value.value:
(function execute(inputs, outputs) {

  var gameID = inputs.gamePlayerGr.getValue('game');
  var playerOrder = inputs.gamePlayerGr.getValue('order');

  var url = Constants.INSTANCE_URL +
      '/jeopardy?id=jeopardy_final_player&sysparm_game=' + gameID +
      '&sysparm_player=' + playerOrder;

  gs.info('url=' + url);

  return url;

})(inputs, outputs);

